"use client";

import { useState } from "react";
import Link from "next/link";
import { motion } from "framer-motion";
import { Button } from "@/components/common/Button";
import { SentinelLogo } from "@/components/common/SentinelLogo";
import { Mail, Lock, ArrowRight, Github, Chrome, User, Check } from "lucide-react";

export default function SignupPage() {
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);
        // Simulate signup
        await new Promise(r => setTimeout(r, 1500));
        setIsLoading(false);
        // Redirect to dashboard
        window.location.href = "/dashboard";
    };

    const passwordStrength = password.length >= 8 ? (password.match(/[A-Z]/) && password.match(/[0-9]/) ? "strong" : "medium") : "weak";

    return (
        <div className="min-h-screen flex">
            {/* Left Side - Signup Form */}
            <div className="w-full lg:w-1/2 flex items-center justify-center p-8 bg-[#0a0a0a]">
                <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    className="w-full max-w-md"
                >
                    {/* Logo */}
                    <Link href="/" className="flex items-center gap-2 mb-8">
                        <SentinelLogo size={28} />
                        <span className="text-xl font-bold">Sentinel</span>
                    </Link>

                    <h2 className="text-3xl font-bold mb-2">Create your account</h2>
                    <p className="text-muted-foreground mb-8">
                        Already have an account?{" "}
                        <Link href="/login" className="text-primary hover:underline">
                            Sign in
                        </Link>
                    </p>

                    {/* Social Login */}
                    <div className="grid grid-cols-2 gap-4 mb-8">
                        <Button variant="outline" className="h-12 gap-2">
                            <Github className="h-5 w-5" />
                            GitHub
                        </Button>
                        <Button variant="outline" className="h-12 gap-2">
                            <Chrome className="h-5 w-5" />
                            Google
                        </Button>
                    </div>

                    <div className="relative mb-8">
                        <div className="absolute inset-0 flex items-center">
                            <div className="w-full border-t border-white/10" />
                        </div>
                        <div className="relative flex justify-center text-xs uppercase">
                            <span className="bg-[#0a0a0a] px-4 text-muted-foreground">or continue with email</span>
                        </div>
                    </div>

                    {/* Signup Form */}
                    <form onSubmit={handleSubmit} className="space-y-5">
                        <div className="space-y-2">
                            <label className="text-sm font-medium">Full Name</label>
                            <div className="relative">
                                <User className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                                <input
                                    type="text"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    placeholder="John Doe"
                                    className="w-full h-12 pl-11 pr-4 bg-white/5 border border-white/10 rounded-xl text-white placeholder:text-muted-foreground/50 focus:outline-none focus:border-primary/50 focus:ring-2 focus:ring-primary/20 transition-all"
                                    required
                                />
                            </div>
                        </div>

                        <div className="space-y-2">
                            <label className="text-sm font-medium">Work Email</label>
                            <div className="relative">
                                <Mail className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                                <input
                                    type="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder="you@company.com"
                                    className="w-full h-12 pl-11 pr-4 bg-white/5 border border-white/10 rounded-xl text-white placeholder:text-muted-foreground/50 focus:outline-none focus:border-primary/50 focus:ring-2 focus:ring-primary/20 transition-all"
                                    required
                                />
                            </div>
                        </div>

                        <div className="space-y-2">
                            <label className="text-sm font-medium">Password</label>
                            <div className="relative">
                                <Lock className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                                <input
                                    type="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    placeholder="Create a strong password"
                                    className="w-full h-12 pl-11 pr-4 bg-white/5 border border-white/10 rounded-xl text-white placeholder:text-muted-foreground/50 focus:outline-none focus:border-primary/50 focus:ring-2 focus:ring-primary/20 transition-all"
                                    required
                                />
                            </div>
                            {/* Password Strength Indicator */}
                            {password && (
                                <div className="flex items-center gap-2 mt-2">
                                    <div className="flex-1 flex gap-1">
                                        <div className={`h-1 flex-1 rounded-full ${passwordStrength === "weak" ? "bg-red-500" : "bg-green-500"}`} />
                                        <div className={`h-1 flex-1 rounded-full ${passwordStrength === "medium" || passwordStrength === "strong" ? "bg-green-500" : "bg-white/10"}`} />
                                        <div className={`h-1 flex-1 rounded-full ${passwordStrength === "strong" ? "bg-green-500" : "bg-white/10"}`} />
                                    </div>
                                    <span className={`text-xs ${passwordStrength === "weak" ? "text-red-400" : passwordStrength === "medium" ? "text-yellow-400" : "text-green-400"}`}>
                                        {passwordStrength}
                                    </span>
                                </div>
                            )}
                        </div>

                        <Button
                            type="submit"
                            className="w-full h-12 text-base font-semibold"
                            disabled={isLoading}
                        >
                            {isLoading ? (
                                <div className="flex items-center gap-2">
                                    <div className="h-4 w-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                                    Creating account...
                                </div>
                            ) : (
                                <div className="flex items-center gap-2">
                                    Get Started Free
                                    <ArrowRight className="h-4 w-4" />
                                </div>
                            )}
                        </Button>
                    </form>

                    <p className="mt-6 text-center text-xs text-muted-foreground">
                        By creating an account, you agree to our{" "}
                        <Link href="/terms" className="underline hover:text-white">Terms</Link>
                        {" "}and{" "}
                        <Link href="/privacy" className="underline hover:text-white">Privacy Policy</Link>
                    </p>
                </motion.div>
            </div>

            {/* Right Side - Features */}
            <div className="hidden lg:flex lg:w-1/2 relative bg-gradient-to-bl from-[#0a0a0a] via-[#0f172a] to-[#0a0a0a] items-center justify-center p-12">
                {/* Background Pattern */}
                <div className="absolute inset-0 opacity-30">
                    <div className="absolute inset-0 bg-[radial-gradient(circle_at_70%_30%,rgba(34,211,238,0.15),transparent_50%)]" />
                    <div className="absolute inset-0 bg-[radial-gradient(circle_at_30%_70%,rgba(168,85,247,0.1),transparent_50%)]" />
                </div>

                <div className="relative z-10 max-w-lg">
                    <motion.h1
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        className="text-4xl font-bold mb-6 leading-tight"
                    >
                        Start your journey to<br />
                        <span className="text-gradient-primary">zero-downtime ops</span>
                    </motion.h1>

                    <motion.p
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.1 }}
                        className="text-muted-foreground text-lg mb-10"
                    >
                        Join thousands of teams who trust Sentinel to keep their systems running smoothly.
                    </motion.p>

                    {/* Features List */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.2 }}
                        className="space-y-4"
                    >
                        {[
                            "Unlimited services monitoring",
                            "AI-powered incident detection",
                            "Automatic self-healing workflows",
                            "Real-time alerting & notifications",
                            "Full audit trail & compliance logs",
                        ].map((feature, index) => (
                            <motion.div
                                key={index}
                                initial={{ opacity: 0, x: 20 }}
                                animate={{ opacity: 1, x: 0 }}
                                transition={{ delay: 0.3 + index * 0.1 }}
                                className="flex items-center gap-3"
                            >
                                <div className="h-6 w-6 rounded-full bg-primary/20 flex items-center justify-center">
                                    <Check className="h-3.5 w-3.5 text-primary" />
                                </div>
                                <span className="text-white">{feature}</span>
                            </motion.div>
                        ))}
                    </motion.div>

                    {/* Testimonial */}
                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.8 }}
                        className="mt-12 p-6 bg-white/5 rounded-2xl border border-white/10"
                    >
                        <p className="text-white/80 italic mb-4">
                            "Sentinel reduced our MTTR by 85%. Our on-call engineers finally get to sleep through the night."
                        </p>
                        <div className="flex items-center gap-3">
                            <div className="h-10 w-10 rounded-full bg-gradient-to-br from-primary to-purple-500" />
                            <div>
                                <div className="font-semibold text-white">Sarah Chen</div>
                                <div className="text-xs text-muted-foreground">VP of Engineering, TechCorp</div>
                            </div>
                        </div>
                    </motion.div>
                </div>
            </div>
        </div>
    );
}

"use client";

import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from "recharts";

const data = Array.from({ length: 24 }, (_, i) => ({
    time: `${i}:00`,
    requests: Math.floor(Math.random() * 5000) + 1000,
    errors: Math.floor(Math.random() * 100),
}));

export function TrafficChart() {
    return (
        <div className="h-[200px] w-full">
            <ResponsiveContainer width="100%" height="100%">
                <BarChart data={data}>
                    <XAxis
                        dataKey="time"
                        stroke="#666"
                        fontSize={10}
                        tickLine={false}
                        axisLine={false}
                        interval={3}
                    />
                    <Tooltip
                        cursor={{ fill: 'rgba(255,255,255,0.05)' }}
                        contentStyle={{ backgroundColor: "#111", border: "1px solid rgba(255,255,255,0.1)", borderRadius: "8px" }}
                        itemStyle={{ fontSize: "12px" }}
                    />
                    <Bar dataKey="requests" name="Requests" radius={[2, 2, 0, 0]}>
                        {data.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={entry.requests > 4000 ? "#c084fc" : "#2dd4bf"} />
                        ))}
                    </Bar>
                </BarChart>
            </ResponsiveContainer>
        </div>
    );
}

"use client";

import { Brain, Sparkles } from "lucide-react";
import { Spotlight } from "@/components/common/Spotlight";

export interface AIInsightItem {
    id: number;
    analysis: string;
    metrics?: any;
    summary?: string;
    timestamp: string;
}

interface AIInsightsProps {
    insights: AIInsightItem[];
}

export function AIInsights({ insights }: AIInsightsProps) {
    const latestInsight = insights[0];

    return (
        <Spotlight className="bg-card border-border h-full flex flex-col">
            <div className="p-4 border-b border-border bg-gradient-to-r from-purple-500/10 to-transparent flex items-center justify-between">
                <div className="flex items-center gap-2">
                    <div className="p-1.5 bg-purple-500/20 rounded-md">
                        <Brain className="h-4 w-4 text-purple-400" />
                    </div>
                    <h3 className="font-semibold text-foreground">AI Sentinel Analysis</h3>
                </div>
                {latestInsight && (
                    <span className="text-xs text-muted-foreground font-mono">
                        {new Date(latestInsight.timestamp).toLocaleTimeString()}
                    </span>
                )}
            </div>

            <div className="p-5 flex-1">
                {latestInsight ? (
                    <div className="space-y-4">
                        <div className="prose prose-invert prose-sm max-w-none">
                            <div className="text-sm text-foreground/80 leading-relaxed whitespace-pre-wrap">
                                {latestInsight.analysis}
                            </div>
                        </div>

                        {latestInsight.metrics && (
                            <div className="grid grid-cols-2 gap-3 mt-4">
                                {Object.entries(latestInsight.metrics).map(([key, val]: [string, any]) => (
                                    <div key={key} className="bg-muted/50 p-2 rounded border border-border text-center">
                                        <div className="text-[10px] text-muted-foreground uppercase">{key}</div>
                                        <div className={`font-mono font-bold ${val.code >= 500 ? 'text-red-500' : 'text-green-500'}`}>
                                            {val.code}
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                ) : (
                    <div className="flex flex-col items-center justify-center h-full py-8 text-muted-foreground">
                        <Sparkles className="h-8 w-8 mb-3 opacity-20" />
                        <p className="text-sm">Waiting for AI Insights...</p>
                        <p className="text-xs opacity-50">Monitoring system streams</p>
                    </div>
                )}
            </div>
        </Spotlight>
    );
}

import React from 'react';
import { Container } from '../../hooks/useContainers';
import { MoreHorizontal, Box, RefreshCw, Layers, Calendar, Network } from "lucide-react";
import { Button } from "@/components/common/Button";
import { cn } from "@/lib/utils";
import { Spotlight } from "@/components/common/Spotlight";
import { Sparkline } from "@/components/common/Sparkline";

interface ContainerCardProps {
    container: Container;
    onRestart: (id: string) => void;
}

const StatusDot = ({ status }: { status: "healthy" | "unhealthy" | "unknown" }) => {
    const color = {
        healthy: "bg-green-500",
        unknown: "bg-gray-500",
        unhealthy: "bg-red-500",
    }[status] || "bg-gray-500";

    return (
        <div className="relative flex h-3 w-3">
            <span className={cn("animate-ping absolute inline-flex h-full w-full rounded-full opacity-75", color)}></span>
            <span className={cn("relative inline-flex rounded-full h-3 w-3", color)}></span>
        </div>
    );
};

export function ContainerCard({ container, onRestart }: ContainerCardProps) {
    const isHealthy = container.health === 'healthy';

    // Mock sparkline data since we don't have history yet
    const mockTrend = React.useMemo(() =>
        Array.from({ length: 12 }, () => 20 + Math.random() * 10),
        []);

    return (
        <Spotlight className="p-5 bg-card border-border hover:border-primary/20 transition-all group">
            <div className="flex justify-between items-start mb-4">
                <div className="flex items-center gap-3">
                    <div className="p-2 rounded-lg bg-muted border border-border">
                        <Box className={`h-4 w-4 ${isHealthy ? 'text-primary' : 'text-red-500'}`} />
                    </div>
                    <div>
                        <h4 className="font-semibold text-sm text-foreground">{container.name}</h4>
                        <div className="flex items-center gap-2 mt-1">
                            <StatusDot status={container.health} />
                            <span className="text-xs text-muted-foreground capitalize">{container.health}</span>
                        </div>
                    </div>
                </div>
                <Button
                    variant="ghost"
                    size="icon"
                    className="h-8 w-8 text-muted-foreground hover:text-primary transition-colors"
                    onClick={() => {
                        if (window.confirm(`Are you sure you want to restart container ${container.name}?`)) {
                            onRestart(container.id);
                        }
                    }}
                    title="Restart Container"
                >
                    <RefreshCw className="h-4 w-4" />
                </Button>
            </div>

            <div className="grid grid-cols-3 gap-2 mb-4">
                <div className="p-2 rounded bg-muted overflow-hidden">
                    <p className="text-[10px] text-muted-foreground uppercase tracking-wider flex items-center gap-1">
                        <Layers size={10} /> Image
                    </p>
                    <p className="text-sm font-mono text-foreground truncate" title={container.image}>
                        {container.image}
                    </p>
                </div>
                <div className="p-2 rounded bg-muted overflow-hidden">
                    <p className="text-[10px] text-muted-foreground uppercase tracking-wider flex items-center gap-1">
                        <Calendar size={10} /> Created at
                    </p>
                    <p className="text-sm font-mono text-foreground truncate">
                        {new Date(container.created).toLocaleDateString(undefined, { month: 'numeric', day: 'numeric' })}
                    </p>
                </div>
                <div className="p-2 rounded bg-muted overflow-hidden">
                    <p className="text-[10px] text-muted-foreground uppercase tracking-wider flex items-center gap-1">
                        <Network size={10} /> Port
                    </p>
                    <p className="text-sm font-mono text-foreground truncate">
                        {container.ports && container.ports.length > 0 ? container.ports[0].PublicPort || container.ports[0].PrivatePort : 'N/A'}
                    </p>
                </div>
            </div>

            {/* Mini Sparkline */}
            <div className="h-10 w-full opacity-50 group-hover:opacity-100 transition-opacity mt-2">
                <Sparkline
                    data={mockTrend.map(val => ({ value: val }))}
                    color={isHealthy ? "#22d3ee" : "#ef4444"}
                    height={40}
                />
            </div>
        </Spotlight>
    );
}

"use client";

import { Incident } from "@/lib/mockData";
import { getSeverityColor, getStatusColor } from "@/lib/theme";
import {
    AlertTriangle,
    CheckCircle,
    CircleX,
    ChevronDown,
    ChevronUp,
    Zap,
    Clock,
    Search
} from "lucide-react";
import { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { Button } from "@/components/common/Button";

interface IncidentCardProps {
    incident: Incident;
    onViewReasoning?: (id: string) => void;
}

export function IncidentCard({ incident, onViewReasoning }: IncidentCardProps) {
    const [expanded, setExpanded] = useState(false);

    const getStatusIcon = () => {
        // Map incident status to theme status for color retrieval
        const statusMap: Record<string, "healthy" | "warning" | "critical"> = {
            "resolved": "healthy",
            "in-progress": "warning",
            "failed": "critical"
        };
        const themeStatus = statusMap[incident.status] || "unknown";
        const color = getStatusColor(themeStatus);

        switch (incident.status) {
            case "resolved": return <CheckCircle className="h-5 w-5 text-green-500" />;
            case "in-progress": return <AlertTriangle className="h-5 w-5 text-orange-500" />;
            case "failed": return <CircleX className="h-5 w-5 text-red-500" />;
        }
    };

    const severityColor = getSeverityColor(incident.severity);

    return (
        <div className="bg-white/5 border border-white/10 rounded-xl overflow-hidden transition-all hover:border-white/20">
            {/* Header / Summary */}
            <div
                className="p-4 cursor-pointer"
                onClick={() => setExpanded(!expanded)}
            >
                <div className="flex items-start justify-between gap-4">
                    <div className="flex items-start gap-3">
                        <div className="mt-1">{getStatusIcon()}</div>
                        <div>
                            <div className="flex items-center gap-2 mb-1">
                                <span className={`text-xs px-2 py-0.5 rounded-full border uppercase tracking-wider font-semibold ${severityColor.bg} ${severityColor.text} ${severityColor.border}`}>
                                    {incident.severity}
                                </span>
                                <span className="text-sm text-muted-foreground">ΓÇó</span>
                                <span className="text-sm text-muted-foreground">{incident.timestamp.split('T')[0]}</span>
                            </div>
                            <h4 className={`font-semibold text-lg leading-tight mb-1 ${incident.severity === 'critical' ? 'text-red-400' :
                                incident.severity === 'warning' ? 'text-yellow-400' : 'text-white'
                                }`}>
                                {incident.title}
                            </h4>
                            <div className="flex items-center gap-4 text-sm text-muted-foreground">
                                <span className="flex items-center gap-1">
                                    <Clock className="h-3 w-3" /> {incident.duration}
                                </span>
                                <span className="flex items-center gap-1 text-primary">
                                    <Zap className="h-3 w-3" /> {incident.agentAction}
                                </span>
                            </div>
                        </div>
                    </div>
                    <Button variant="ghost" size="icon" className="text-muted-foreground">
                        {expanded ? <ChevronUp className="h-4 w-4" /> : <ChevronDown className="h-4 w-4" />}
                    </Button>
                </div>
            </div>

            {/* Expanded Details */}
            <AnimatePresence>
                {expanded && (
                    <motion.div
                        initial={{ height: 0, opacity: 0 }}
                        animate={{ height: "auto", opacity: 1 }}
                        exit={{ height: 0, opacity: 0 }}
                        className="border-t border-white/5 bg-black/20"
                    >
                        <div className="p-4 space-y-4">
                            {/* Root Cause */}
                            <div className="grid grid-cols-[100px_1fr] gap-4 text-sm">
                                <span className="text-muted-foreground">Root Cause:</span>
                                <span className="text-white font-medium">{incident.rootCause}</span>
                            </div>

                            {/* Confidence */}
                            <div className="grid grid-cols-[100px_1fr] gap-4 text-sm items-center">
                                <span className="text-muted-foreground">Confidence:</span>
                                <div className="flex items-center gap-2">
                                    <div className="h-1.5 w-24 bg-white/10 rounded-full overflow-hidden">
                                        <div
                                            className="h-full bg-primary"
                                            style={{ width: `${incident.agentPredictionConfidence}%` }}
                                        />
                                    </div>
                                    <span className="text-primary font-mono">{incident.agentPredictionConfidence}%</span>
                                </div>
                            </div>

                            {/* Timeline */}
                            <div className="pt-2">
                                <h5 className="text-xs font-semibold text-muted-foreground uppercase tracking-wider mb-3">Incident Timeline</h5>
                                <div className="space-y-3 relative pl-4 border-l border-white/10 ml-2">
                                    {incident.timeline.map((event, idx) => (
                                        <div key={idx} className="relative group">
                                            <div className="absolute -left-[25px] top-0 h-6 w-6 rounded-full bg-background border border-white/10 flex items-center justify-center z-10">
                                                <span className="text-[10px] filter grayscale group-hover:grayscale-0 transition-all leading-none ml-[0.5px]">{event.icon}</span>
                                            </div>
                                            <div className="flex flex-col">
                                                <span className="text-xs text-muted-foreground font-mono mb-0.5">{event.time}</span>
                                                <span className="text-sm text-white">{event.event}</span>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>

                            {/* Actions */}
                            <div className="flex justify-end pt-2 gap-2">
                                <Button size="sm" variant="outline" onClick={(e) => {
                                    e.stopPropagation();
                                    if (onViewReasoning) onViewReasoning(incident.id);
                                }}>
                                    <Search className="h-3 w-3 mr-2" />
                                    View Agent Reasoning
                                </Button>
                            </div>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

"use client";

import { useState } from "react";
import {
    LineChart,
    Line,
    XAxis,
    YAxis,
    CartesianGrid,
    Tooltip,
    ResponsiveContainer,
    BarChart,
    Bar,
    Legend,
} from "recharts";
import { ServiceMetrics } from "@/hooks/useMetrics";

interface MetricsChartsProps {
    metrics: Record<string, ServiceMetrics>;
}

export function MetricsCharts({ metrics }: MetricsChartsProps) {
    const [timeRange, setTimeRange] = useState<"1h" | "6h" | "24h">("1h");
    const [activeTab, setActiveTab] = useState<"response" | "error" | "resources">("response");

    // Transform metrics dictionary into array for charting if needed
    // But for multi-line charts, we usually iterate over the keys to create multiple Lines
    const serviceIds = Object.keys(metrics);

    // Helper to get color for service
    const getServiceColor = (id: string) => {
        // If service is down, line becomes RED
        if (metrics[id] && metrics[id].currentErrorRate > 0.5) return "#ef4444";

        switch (id) {
            case "api-gateway": return "#22d3ee"; // Cyan
            case "auth-service": return "#818cf8"; // Indigo
